  using SoftGear.Strix.Client.Core.Model.Manager.Filter.Builder;
  using SoftGear.Strix.Unity.Runtime;
  using UnityEngine;
 
  public class StrixSearchRoomExample : MonoBehaviour
  {
      void Start()
      {
          var strixNetwork = StrixNetwork.instance;
 
          strixNetwork.applicationId = "00000000-0000-0000-0000-000000000000";
          strixNetwork.ConnectMasterServer(
              host: "0123456789abcdef01234567.game.strixcloud.net",
              connectEventHandler: _ => {
                  Debug.Log("Connection established.");
 
                  strixNetwork.CreateRoom(
                      new RoomProperties { name = "My Game Room" },
                      new RoomMemberProperties { name = "My Player Name" },
                      handler: __ => {
                          Debug.Log("Room 1 created.");
 
                          strixNetwork.SearchRoom(
                              condition: ConditionBuilder.Builder().Field("name").EqualTo("My Game Room").Build(),
                              limit: 10,
                              offset: 0,
                              handler: searchResults => {
                                  Debug.Log(searchResults.roomInfoCollection.Count + " rooms found.");
 
                                  foreach (var roomInfo in searchResults.roomInfoCollection)
                                      Debug.Log("Room ID: " + roomInfo.id
                                          + "\nHost: " + roomInfo.host
                                          + "\nMember count: " + roomInfo.memberCount
                                          + "\nCapacity: " + roomInfo.capacity
                                      );
                              },
                              failureHandler: searchError => Debug.LogError("Search failed. Reason: " + searchError.cause));
                      },
                      failureHandler: createRoomError => Debug.LogError("Could not create room. Reason: " + createRoomError.cause)
                  );
              },
              errorEventHandler: connectError => Debug.LogError("Connection failed. Reason: " + connectError.cause)
          );
      }
  }

Note: Make sure to change the placeholder values of  and host to the real ones that can be found on the Strix Cloud application information tab.

  using SoftGear.Strix.Client.Core.Model.Manager.Filter.Builder;
  using SoftGear.Strix.Unity.Runtime;
  using UnityEngine;
  
  public class StrixSearchRoomExample : MonoBehaviour
  {
      void Start()
      {
          var strixNetwork = StrixNetwork.instance;
  
          strixNetwork.applicationId = "00000000-0000-0000-0000-000000000000";
          strixNetwork.ConnectMasterServer(
              host: "0123456789abcdef01234567.game.strixcloud.net",
              connectEventHandler: _ => {
                  Debug.Log("Connection established.");
  
                  strixNetwork.CreateRoom(
                      new RoomProperties { name = "My Game Room" },
                      new RoomMemberProperties { name = "My Player Name" },
                      handler: __ => {
                          Debug.Log("Room created.");
  
                          strixNetwork.SearchRoom(
                              condition: ConditionBuilder.Builder().Field("name").EqualTo("My Game Room").Build(),
                              order: new Order("memberCount", OrderType.Ascending),
                              limit: 10,
                              offset: 0,
                              handler: searchResults => {
                                  Debug.Log(searchResults.roomInfoCollection.Count + " rooms found.");
  
                                  foreach (var roomInfo in searchResults.roomInfoCollection)
                                      Debug.Log("Room ID: " + roomInfo.id
                                          + "\nHost: " + roomInfo.host
                                          + "\nMember count: " + roomInfo.memberCount
                                          + "\nCapacity: " + roomInfo.capacity
                                      );
                              },
                              failureHandler: searchError => Debug.LogError("Search failed. Reason: " + searchError.cause)
                          );
                      },
                      failureHandler: createRoomError => Debug.LogError("Could not create room. Reason: " + createRoomError.cause)
                  );
              },
              errorEventHandler: connectError => Debug.LogError("Connection failed. Reason: " + connectError.cause)
          );
      }
  }

              In this example we first connect to a master server to be able to create a new room. Then we create a match room for up to 20 members, give it a name, set a password, and a description.
              Strix match rooms have 1  property (stringKey) and 8  properties (key1, ..., key8)) that can be set to any value and later be used in a search criteria.
              In the current example we use key1 to signify the difficulty of the game match and set it to 4.0.
              Since we define description as a custom property, we cannot use it in a search condition, but we can still read its value in the search results.
              After creating the room we perform a search for all rooms that have difficulty set to 2.0 or greater to check if the room was created correctly.
              Then we output the information about the found room to see if the properties we have set are readable.

              Note: Make sure to change the placeholder values of  and host to the real ones that can be found on the Strix Cloud application information tab.
              
                using SoftGear.Strix.Client.Core.Model.Manager.Filter;
                using SoftGear.Strix.Client.Core.Model.Manager.Filter.Builder;
                using SoftGear.Strix.Unity.Runtime;
                using System.Collections.Generic;
                using UnityEngine;
                
                public class StrixCreateRoomExample : MonoBehaviour
                {
                    void Start()
                    {
                        var strixNetwork = StrixNetwork.instance;
                
                        strixNetwork.applicationId = "00000000-0000-0000-0000-000000000000";
                        strixNetwork.ConnectMasterServer(
                            host: "0123456789abcdef01234567.game.strixcloud.net",
                            connectEventHandler: _ => {
                                Debug.Log("Connection established.");
                
                                strixNetwork.CreateRoom(
                                    new RoomProperties {
                                        name = "Fire Tournament",
                                        password = "66e3f2nk",
                                        capacity = 20,
                                        key1 = 4.0,
                                        properties = new Dictionary<string, object> { { "description", "The first major tournament of the new season." } }
                                    },
                                    new RoomMemberProperties { name = "Braille" },
                                    handler: __ => {
                                        Debug.Log("Room created.");
                
                                        strixNetwork.SearchRoom(
                                            condition: ConditionBuilder.Builder().Field("key1").GreaterThan(2.0).Build(),
                                            order: new Order("memberCount", OrderType.Ascending),
                                            limit: 10,
                                            offset: 0,
                                            handler: searchResults => {
                                                var foundRooms = searchResults.roomInfoCollection;
                                                Debug.Log(foundRooms.Count + " rooms found.");
                
                                                foreach (var roomInfo in foundRooms)
                                                    Debug.Log("Room ID: " + roomInfo.id
                                                        + "\nHost: " + roomInfo.host
                                                        + "\nMember count: " + roomInfo.memberCount
                                                        + "\nCapacity: " + roomInfo.capacity
                                                        + "\nDifficulty: " + roomInfo.key1
                                                        + "\nDescription: " + (roomInfo.properties != null && roomInfo.properties.ContainsKey("description") ? roomInfo.properties["description"] : "None")
                                                    );
                                            },
                                            failureHandler: searchError => Debug.LogError("Search failed. Reason: " + searchError.cause)
                                        );
                                    },
                                    failureHandler: createRoomError => Debug.LogError("Could not create room. Reason: " + createRoomError.cause)
                                );
                            },
                            errorEventHandler: connectError => Debug.LogError("Connection failed. Reason: " + connectError.cause)
                        );
                    }
                }

